import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { Visite } from '../../types';

const VisitListScreen = () => {
  const [visits, setVisits] = useState<Visite[]>([]);
  const navigation = useNavigation();

  // Exemple de données (à remplacer par l'appel API)
  useEffect(() => {
    setVisits([
      {
        id: 1,
        date: '2024-03-13',
        surRendezVous: true,
        heureArrivee: '14:00',
        heureDebut: '14:15',
        heureDepart: '15:00',
        medecinId: 1,
        visiteurId: 1
      }
    ]);
  }, []);

  const renderItem = ({ item }: { item: Visite }) => (
    <TouchableOpacity 
      style={styles.visitCard}
      onPress={() => navigation.navigate('VisitDetail', { visitId: item.id })}
    >
      <Text style={styles.date}>Date: {new Date(item.date).toLocaleDateString()}</Text>
      <Text>Rendez-vous: {item.surRendezVous ? 'Oui' : 'Non'}</Text>
      <Text>Arrivée: {item.heureArrivee}</Text>
      <Text>Début: {item.heureDebut}</Text>
      <Text>Départ: {item.heureDepart}</Text>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={visits}
        renderItem={renderItem}
        keyExtractor={item => item.id.toString()}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 10,
    backgroundColor: '#f5f5f5'
  },
  visitCard: {
    backgroundColor: '#fff',
    padding: 15,
    marginVertical: 8,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3
  },
  date: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 8
  }
});

export default VisitListScreen;
