// filepath: /GSBVTTMobile/GSBVTTMobile/src/screens/visits/VisitDetailScreen.tsx

import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, ActivityIndicator } from 'react-native';
import { fetchVisitDetails } from '../../api'; // Assuming this function is defined in your API module
import { RouteProp, useRoute } from '@react-navigation/native';

type VisitDetailScreenRouteProp = RouteProp<{ params: { visitId: string } }, 'params'>;

const VisitDetailScreen: React.FC = () => {
  const route = useRoute<VisitDetailScreenRouteProp>();
  const { visitId } = route.params;
  const [visitDetails, setVisitDetails] = useState<any>(null); // Replace 'any' with your visit type
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const getVisitDetails = async () => {
      try {
        const details = await fetchVisitDetails(visitId);
        setVisitDetails(details);
      } catch (error) {
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    getVisitDetails();
  }, [visitId]);

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#0000ff" />
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{visitDetails.title}</Text>
      <Text style={styles.description}>{visitDetails.description}</Text>
      {/* Add more fields as necessary */}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#fff',
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  description: {
    fontSize: 16,
    marginTop: 8,
  },
});

export default VisitDetailScreen;
