import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity, RefreshControl } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { fetchVisites } from '../../services/api';

interface ApiVisite {
  id_visite: number;          // Changé de 'id' à 'id_visite'
  date_visite: string;        // Changé de 'date' à 'date_visite'
  rendez_vous: boolean;       // Changé de 'surRendezVous' à 'rendez_vous'
  heure_arrivee: string;      // Changé le format du nom
  heure_debut_entretien: string;  // Nouveau nom de champ
  heure_depart: string;
  id_medecin: number;
  id_visiteur: number;
}

const VisitListScreen = () => {
  const [visits, setVisits] = useState<ApiVisite[]>([]);
  const [refreshing, setRefreshing] = useState(false);
  const [error, setError] = useState('');
  const navigation = useNavigation();

  const loadVisites = async () => {
    try {
      setError('');
      const data = await fetchVisites();
      console.log('Données reçues:', data); // Pour débugger
      setVisits(Array.isArray(data) ? data : []);
    } catch (err) {
      setError('Erreur lors du chargement des visites');
      console.error('Erreur loadVisites:', err);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadVisites();
    setRefreshing(false);
  };

  useEffect(() => {
    loadVisites();
  }, []);

  const renderItem = ({ item }: { item: ApiVisite }) => (
    <TouchableOpacity 
      style={styles.visitCard}
      onPress={() => navigation.navigate('VisitDetail', { visitId: item.id_visite })}
    >
      <Text style={styles.dateText}>
        {new Date(item.date_visite).toLocaleDateString()}
      </Text>
      <Text>Rendez-vous: {item.rendez_vous ? 'Oui' : 'Non'}</Text>
      <Text>Arrivée: {item.heure_arrivee}</Text>
      <Text>Début: {item.heure_debut_entretien}</Text>
      <Text>Départ: {item.heure_depart}</Text>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      {error ? (
        <View style={styles.errorContainer}>
          <Text style={styles.error}>{error}</Text>
          <TouchableOpacity style={styles.retryButton} onPress={loadVisites}>
            <Text style={styles.retryText}>Réessayer</Text>
          </TouchableOpacity>
        </View>
      ) : (
        <FlatList
          data={visits}
          renderItem={renderItem}
          keyExtractor={item => item.id_visite.toString()}
          refreshControl={
            <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
          }
          ListEmptyComponent={
            <Text style={styles.emptyText}>Aucune visite trouvée</Text>
          }
        />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    alignItems: 'center',
    justifyContent: 'center',
  },
  visitCard: {
    backgroundColor: '#fff',
    padding: 15,
    marginHorizontal: 10,
    marginVertical: 5,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  dateText: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  error: {
    color: 'red',
    marginBottom: 10,
  },
  retryButton: {
    backgroundColor: '#007AFF',
    padding: 10,
    borderRadius: 5,
  },
  retryText: {
    color: 'white',
  },
  errorContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  emptyText: {
    textAlign: 'center',
    marginTop: 50,
    fontSize: 16,
    color: '#666',
  }
});

export default VisitListScreen;
