import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import HomeScreen from './screens/home/HomeScreen';
import VisitListScreen from './screens/visits/VisitListScreen';
import AddVisitScreen from './screens/visits/AddVisitScreen';

const Stack = createStackNavigator();

const App = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator 
        initialRouteName="Home"
        screenOptions={{
          headerStyle: {
            backgroundColor: '#007AFF',
          },
          headerTintColor: '#fff',
          headerTitleStyle: {
            fontWeight: 'bold',
          },
        }}
      >
        <Stack.Screen 
          name="Home" 
          component={HomeScreen}
          options={{ title: 'GSB - Accueil' }}
        />
        <Stack.Screen 
          name="VisitList" 
          component={VisitListScreen}
          options={{ title: 'Mes Visites' }}
        />
        <Stack.Screen 
          name="AddVisit" 
          component={AddVisitScreen}
          options={{ title: 'Nouvelle Visite' }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
};

export default App;
