import React, { useEffect, useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import AsyncStorage from '@react-native-async-storage/async-storage';
import LoginScreen from './screens/auth/LoginScreen';
import HomeScreen from './screens/home/HomeScreen';
import VisitListScreen from './screens/visits/VisitListScreen';
import AddVisitScreen from './screens/visits/AddVisitScreen';
import VisitDetailScreen from './screens/visits/VisitDetailScreen';

const Stack = createStackNavigator();

const App = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [userToken, setUserToken] = useState(null);

  useEffect(() => {
    const checkToken = async () => {
      try {
        const token = await AsyncStorage.getItem('userToken');
        setUserToken(token);
      } catch (e) {
        console.error(e);
      } finally {
        setIsLoading(false);
      }
    };
    checkToken();
  }, []);

  if (isLoading) {
    return null;
  }

  return (
    <NavigationContainer>
      <Stack.Navigator>
        {userToken == null ? (
          <Stack.Screen 
            name="Login" 
            component={LoginScreen}
            options={{ headerShown: false }}
          />
        ) : (
          <>
            <Stack.Screen 
              name="Home" 
              component={HomeScreen}
              options={{ title: 'GSB - Accueil' }}
            />
            <Stack.Screen 
              name="VisitList" 
              component={VisitListScreen}
              options={{ title: 'Mes Visites' }}
            />
            <Stack.Screen 
              name="AddVisit" 
              component={AddVisitScreen}
              options={{ title: 'Nouvelle Visite' }}
            />
            <Stack.Screen 
              name="VisitDetail" 
              component={VisitDetailScreen}
              options={{ title: 'Détail de la visite' }}
            />
          </>
        )}
      </Stack.Navigator>
    </NavigationContainer>
  );
};

export default App;
