import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, ScrollView, Switch } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { Medecin, Cabinet } from '../../types';

const AddVisitScreen = () => {
  const navigation = useNavigation();
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);
  const [surRendezVous, setSurRendezVous] = useState(false);
  const [heureArrivee, setHeureArrivee] = useState('');
  const [heureDebut, setHeureDebut] = useState('');
  const [heureDepart, setHeureDepart] = useState('');
  const [selectedCabinet, setSelectedCabinet] = useState<Cabinet | null>(null);
  const [selectedMedecin, setSelectedMedecin] = useState<Medecin | null>(null);

  const handleSubmit = () => {
    // TODO: Implémenter la logique d'envoi à l'API
    navigation.goBack();
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.inputGroup}>
        <Text style={styles.label}>Date de la visite</Text>
        <TextInput
          style={styles.input}
          value={date}
          onChangeText={setDate}
          placeholder="YYYY-MM-DD"
        />
      </View>

      <View style={styles.inputGroup}>
        <Text style={styles.label}>Sur rendez-vous</Text>
        <Switch
          value={surRendezVous}
          onValueChange={setSurRendezVous}
        />
      </View>

      <View style={styles.inputGroup}>
        <Text style={styles.label}>Heure d'arrivée</Text>
        <TextInput
          style={styles.input}
          value={heureArrivee}
          onChangeText={setHeureArrivee}
          placeholder="HH:MM"
        />
      </View>

      <View style={styles.inputGroup}>
        <Text style={styles.label}>Heure de début</Text>
        <TextInput
          style={styles.input}
          value={heureDebut}
          onChangeText={setHeureDebut}
          placeholder="HH:MM"
        />
      </View>

      <View style={styles.inputGroup}>
        <Text style={styles.label}>Heure de départ</Text>
        <TextInput
          style={styles.input}
          value={heureDepart}
          onChangeText={setHeureDepart}
          placeholder="HH:MM"
        />
      </View>

      <TouchableOpacity
        style={styles.submitButton}
        onPress={handleSubmit}
      >
        <Text style={styles.submitButtonText}>Enregistrer la visite</Text>
      </TouchableOpacity>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: '#fff'
  },
  inputGroup: {
    marginBottom: 20
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
    fontWeight: '500'
  },
  input: {
    borderWidth: 1,
    borderColor: '#ddd',
    padding: 10,
    borderRadius: 8,
    fontSize: 16
  },
  submitButton: {
    backgroundColor: '#007AFF',
    padding: 15,
    borderRadius: 8,
    marginTop: 20
  },
  submitButtonText: {
    color: 'white',
    textAlign: 'center',
    fontSize: 16,
    fontWeight: 'bold'
  }
});

export default AddVisitScreen;
