import AsyncStorage from '@react-native-async-storage/async-storage';

const BASE_URL = 'https://s5-4242.nuage-peda.fr/gsbvttMobile/API';

interface LoginResponse {
  status: string;
  message: string;
  user: {
    id: number;
    nom: string;
    prenom: string;
    email: string;
  };
  token: string;
}

interface ApiError {
  error: string;
  message?: string;
}

// Ajout des interfaces pour les visites
interface VisiteDetails {
  id_visite: number;
  date_visite: string;
  heure_arrivee: string;
  heure_debut_entretien: string;
  heure_depart: string;
  rendez_vous: boolean;
  id_medecin: number;
  id_visiteur: number;
}

interface CreateVisiteData {
  id_visiteur: number;
  id_medecin: number;
  date_visite: string;
  heure_arrivee: string;
  heure_debut_entretien: string;
  heure_depart: string;
  rendez_vous: boolean;
  temps_attente?: number;
  temps_visite?: number;
}

const formatTime = (time: string): string => {
  // Si l'heure contient déjà les secondes, on la renvoie telle quelle
  if (time.split(':').length === 3) return time;
  // Sinon on ajoute les secondes
  return time + ':00';
};

const formatTimeForAPI = (time: string) => {
  // Convertir le format HH:mm:ss en format datetime MySQL
  return `2000-01-01 ${time}`;
};

export const login = async (email: string, password: string): Promise<LoginResponse> => {
  try {
    const response = await fetch(`${BASE_URL}/ApiAuth.php`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    });

    const data = await response.json();
    
    if (!response.ok) {
      throw new Error(data.message || 'Erreur de connexion');
    }

    return data;
  } catch (error) {
    console.error('Login error:', error);
    throw error;
  }
};

const fetchWithAuth = async (endpoint: string, options: RequestInit = {}) => {
  try {
    const token = await AsyncStorage.getItem('userToken');
    
    if (!token) {
      throw new Error('Token non trouvé');
    }

    const response = await fetch(`${BASE_URL}${endpoint}`, {
      ...options,
      headers: {
        ...options.headers,
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
    });

    const data = await response.json();
    
    if (!response.ok) {
      throw new Error(data.message || 'Erreur API');
    }

    return data;
  } catch (error) {
    console.error('API error:', error);
    throw error;
  }
};

export const fetchVisites = async () => {
  return fetchWithAuth('/ApiVisite.php');
};

const calculateTimes = (arrivee: string, debut: string, depart: string) => {
  try {
    const [hA, mA] = arrivee.split(':').map(Number);
    const [hD, mD] = debut.split(':').map(Number);
    const [hF, mF] = depart.split(':').map(Number);

    const tempsAttente = (hD * 60 + mD) - (hA * 60 + mA);
    const tempsVisite = (hF * 60 + mF) - (hA * 60 + mA);

    return {
      temps_attente: Math.max(0, tempsAttente),
      temps_visite: Math.max(0, tempsVisite)
    };
  } catch (error) {
    console.error('Erreur calcul temps:', error);
    return { temps_attente: 0, temps_visite: 0 };
  }
};

export const createVisite = async (visiteData: CreateVisiteData) => {
  try {
    const { temps_attente, temps_visite } = calculateTimes(
      visiteData.heure_arrivee,
      visiteData.heure_debut_entretien,
      visiteData.heure_depart
    );

    const formattedData = {
      ...visiteData,
      rendez_vous: visiteData.rendez_vous ? 1 : 0, // Convertir le booléen en 0/1
      temps_attente,
      temps_visite
    };

    console.log('Données formatées:', formattedData); // Debug

    const response = await fetchWithAuth('/ApiVisite.php', {
      method: 'POST',
      body: JSON.stringify(formattedData),
    });

    return response;
  } catch (error) {
    console.error('Erreur création visite:', error);
    throw error;
  }
};

export const fetchCabinets = async (latitude?: number, longitude?: number) => {
  const params = latitude && longitude ? `?latitude=${latitude}&longitude=${longitude}` : '';
  return fetchWithAuth(`/ApiCabinet.php${params}`);
};

export const fetchMedecins = async () => {
  return fetchWithAuth('/ApiMedecin.php');
};

export const getVisite = async (id: number): Promise<VisiteDetails> => {
  return fetchWithAuth(`/ApiVisite.php?id=${id}`);
};

export const updateVisite = async (visiteData: VisiteDetails) => {
  return fetchWithAuth('/ApiVisite.php', {
    method: 'PUT',
    body: JSON.stringify(visiteData)
  });
};

export const deleteVisite = async (id: number) => {
  return fetchWithAuth(`/ApiVisite.php`, {
    method: 'DELETE',
    body: JSON.stringify({ id_visite: id }),
  });
};
