import React, { useState, useEffect } from 'react';
import { 
  View, 
  StyleSheet, 
  Alert,
  ScrollView,
  Text,
  ActivityIndicator
} from 'react-native';
import { getVisite } from '../../services/api';

interface VisiteDetails {
  id_visite: number;
  date_visite: string;
  heure_arrivee: string;
  heure_debut_entretien: string;
  heure_depart: string;
  rendez_vous: boolean;
  id_medecin: number;
  id_visiteur: number;
  nom_medecin?: string;
  prenom_medecin?: string;
  temps_attente?: string;
  temps_visite?: string;
}

const VisitDetailScreen = ({ route }) => {
  const { visitId } = route.params;
  const [visite, setVisite] = useState<VisiteDetails | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const loadVisite = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log("Chargement de la visite ID:", visitId);

      const response = await getVisite(visitId);
      console.log("Données brutes reçues:", JSON.stringify(response, null, 2));

      // Prendre le premier élément si c'est un tableau
      const data = Array.isArray(response) ? response[0] : response;

      if (data) {
        setVisite(data);
        console.log("Données formatées:", data);
      } else {
        setError("Aucune donnée reçue");
      }
    } catch (error) {
      console.error("Erreur de chargement:", error);
      setError(`Impossible de charger la visite: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadVisite();
  }, [visitId]);

  const formatTime = (datetime: string | null | undefined): string => {
    if (!datetime) return '-';
    try {
      // Extraire l'heure de la date complète
      const timeMatch = datetime.match(/\d{2}:\d{2}:\d{2}/);
      if (timeMatch) {
        return timeMatch[0].substring(0, 5); // Retourne HH:MM
      }
      return '-';
    } catch (e) {
      console.error("Erreur formatage heure:", e);
      return '-';
    }
  };

  const formatDate = (dateStr: string): string => {
    try {
      const date = new Date(dateStr);
      if (isNaN(date.getTime())) {
        return dateStr;
      }
      return date.toLocaleDateString('fr-FR', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    } catch (e) {
      console.error("Erreur formatage date:", e);
      return dateStr;
    }
  };

  const renderVisitInfo = () => {
    if (!visite) {
      return null;
    }

    return (
      <ScrollView style={styles.container}>
        <View style={styles.card}>
          <Text style={styles.title}>
            Visite du {new Date(visite.date_visite).toLocaleDateString('fr-FR', {
              weekday: 'long',
              year: 'numeric',
              month: 'long',
              day: 'numeric'
            })}
          </Text>

          <View style={styles.section}>
            <Text style={styles.label}>Médecin:</Text>
            <Text style={styles.value}>
              Dr. {visite.prenom_medecin} {visite.nom_medecin}
            </Text>
          </View>

          <View style={styles.section}>
            <Text style={styles.label}>Type de visite:</Text>
            <View style={[styles.badge, visite.rendez_vous ? styles.rdvBadge : styles.noRdvBadge]}>
              <Text style={[styles.badgeText, visite.rendez_vous ? styles.rdvText : styles.noRdvText]}>
                {visite.rendez_vous ? 'Sur rendez-vous' : 'Sans rendez-vous'}
              </Text>
            </View>
          </View>

          <View style={styles.timeSection}>
            <Text style={styles.sectionTitle}>Horaires</Text>
            <View style={styles.timeRow}>
              <Text style={styles.timeLabel}>Arrivée:</Text>
              <Text style={styles.timeValue}>{formatTime(visite.heure_arrivee)}</Text>
            </View>
            <View style={styles.timeRow}>
              <Text style={styles.timeLabel}>Début d'entretien:</Text>
              <Text style={styles.timeValue}>{formatTime(visite.heure_debut_entretien)}</Text>
            </View>
            <View style={styles.timeRow}>
              <Text style={styles.timeLabel}>Départ:</Text>
              <Text style={styles.timeValue}>{formatTime(visite.heure_depart)}</Text>
            </View>
          </View>

          <View style={styles.durationSection}>
            <Text style={styles.sectionTitle}>Durées</Text>
            <View style={styles.timeRow}>
              <Text style={styles.timeLabel}>Temps d'attente:</Text>
              <Text style={styles.timeValue}>{visite.temps_attente?.substring(0, 5) || '-'}</Text>
            </View>
            <View style={styles.timeRow}>
              <Text style={styles.timeLabel}>Durée totale:</Text>
              <Text style={styles.timeValue}>{visite.temps_visite?.substring(0, 5) || '-'}</Text>
            </View>
          </View>
        </View>
      </ScrollView>
    );
  };

  if (loading) {
    return (
      <View style={styles.centerContainer}>
        <ActivityIndicator size="large" color="#1e3c72" />
        <Text>Chargement...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={styles.centerContainer}>
        <Text style={styles.errorText}>{error}</Text>
      </View>
    );
  }

  return renderVisitInfo();
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
    padding: 15,
  },
  centerContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  card: {
    backgroundColor: 'white',
    borderRadius: 10,
    padding: 15,
    elevation: 3,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 20,
    color: '#1e3c72',
  },
  section: {
    marginBottom: 15,
    borderBottomWidth: 1,
    borderBottomColor: '#eee',
    paddingBottom: 10,
  },
  label: {
    fontSize: 16,
    color: '#666',
    marginBottom: 5,
  },
  value: {
    fontSize: 16,
    color: '#333',
    fontWeight: '500',
  },
  errorText: {
    color: 'red',
    fontSize: 16,
    textAlign: 'center',
  },
  timeSection: {
    marginTop: 20,
    padding: 15,
    backgroundColor: '#f8f9fa',
    borderRadius: 8,
  },
  durationSection: {
    marginTop: 15,
    padding: 15,
    backgroundColor: '#f8f9fa',
    borderRadius: 8,
  },
  sectionTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#1e3c72',
    marginBottom: 10,
  },
  timeRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 8,
    borderBottomWidth: 1,
    borderBottomColor: '#eee',
  },
  timeLabel: {
    fontSize: 15,
    color: '#666',
  },
  timeValue: {
    fontSize: 15,
    fontWeight: '500',
    color: '#333',
  },
  badge: {
    padding: 8,
    borderRadius: 16,
    alignSelf: 'flex-start',
    marginTop: 5,
  },
  rdvBadge: {
    backgroundColor: '#e8f5e9',
  },
  noRdvBadge: {
    backgroundColor: '#ffebee',
  },
  rdvText: {
    color: '#2e7d32',
  },
  noRdvText: {
    color: '#c62828',
  },
});

export default VisitDetailScreen;
