import React, { useState, useEffect } from 'react';
import { 
  View, 
  Text, 
  StyleSheet, 
  TextInput, 
  TouchableOpacity, 
  Alert,
  ScrollView,
  Switch
} from 'react-native';
import { getVisite, updateVisite, deleteVisite } from '../../services/api';

const VisitDetailScreen = ({ route, navigation }) => {
  const { visitId } = route.params;
  const [isEditing, setIsEditing] = useState(false);
  const [visite, setVisite] = useState(null);
  const [formData, setFormData] = useState({
    date_visite: '',
    heure_arrivee: '',
    heure_debut_entretien: '',
    heure_depart: '',
    rendez_vous: false
  });

  useEffect(() => {
    loadVisite();
  }, [visitId]);

  const loadVisite = async () => {
    try {
      const data = await getVisite(visitId);
      setVisite(data);
      setFormData({
        date_visite: data.date_visite,
        heure_arrivee: data.heure_arrivee,
        heure_debut_entretien: data.heure_debut_entretien,
        heure_depart: data.heure_depart,
        rendez_vous: data.rendez_vous
      });
    } catch (error) {
      Alert.alert('Erreur', 'Impossible de charger la visite');
    }
  };

  const handleUpdate = async () => {
    try {
      await updateVisite({
        id_visite: visitId,
        ...formData,
        id_visiteur: visite.id_visiteur,
        id_medecin: visite.id_medecin
      });
      Alert.alert('Succès', 'Visite mise à jour');
      setIsEditing(false);
      loadVisite();
    } catch (error) {
      Alert.alert('Erreur', 'Impossible de mettre à jour la visite');
    }
  };

  const handleDelete = async () => {
    Alert.alert(
      'Confirmation',
      'Voulez-vous vraiment supprimer cette visite ?',
      [
        { text: 'Annuler', style: 'cancel' },
        {
          text: 'Supprimer',
          style: 'destructive',
          onPress: async () => {
            try {
              await deleteVisite(visitId);
              navigation.goBack();
            } catch (error) {
              Alert.alert('Erreur', 'Impossible de supprimer la visite');
            }
          }
        }
      ]
    );
  };

  return (
    <ScrollView style={styles.container}>
      {isEditing ? (
        <View style={styles.form}>
          <Text style={styles.label}>Date</Text>
          <TextInput
            style={styles.input}
            value={formData.date_visite}
            onChangeText={(text) => setFormData({...formData, date_visite: text})}
          />

          <Text style={styles.label}>Heure d'arrivée</Text>
          <TextInput
            style={styles.input}
            value={formData.heure_arrivee}
            onChangeText={(text) => setFormData({...formData, heure_arrivee: text})}
          />

          <Text style={styles.label}>Heure de début</Text>
          <TextInput
            style={styles.input}
            value={formData.heure_debut_entretien}
            onChangeText={(text) => setFormData({...formData, heure_debut_entretien: text})}
          />

          <Text style={styles.label}>Heure de départ</Text>
          <TextInput
            style={styles.input}
            value={formData.heure_depart}
            onChangeText={(text) => setFormData({...formData, heure_depart: text})}
          />

          <View style={styles.switchContainer}>
            <Text style={styles.label}>Rendez-vous</Text>
            <Switch
              value={formData.rendez_vous}
              onValueChange={(value) => setFormData({...formData, rendez_vous: value})}
            />
          </View>

          <TouchableOpacity style={styles.saveButton} onPress={handleUpdate}>
            <Text style={styles.buttonText}>Enregistrer</Text>
          </TouchableOpacity>
        </View>
      ) : (
        <View>
          <View style={styles.infoRow}>
            <Text style={styles.label}>Date:</Text>
            <Text style={styles.value}>{visite?.date_visite}</Text>
          </View>
          <View style={styles.infoRow}>
            <Text style={styles.label}>Rendez-vous:</Text>
            <Text style={styles.value}>{visite?.rendez_vous ? 'Oui' : 'Non'}</Text>
          </View>
          <View style={styles.infoRow}>
            <Text style={styles.label}>Heure d'arrivée:</Text>
            <Text style={styles.value}>{visite?.heure_arrivee}</Text>
          </View>
          <View style={styles.infoRow}>
            <Text style={styles.label}>Heure de début:</Text>
            <Text style={styles.value}>{visite?.heure_debut_entretien}</Text>
          </View>
          <View style={styles.infoRow}>
            <Text style={styles.label}>Heure de départ:</Text>
            <Text style={styles.value}>{visite?.heure_depart}</Text>
          </View>
        </View>
      )}

      <View style={styles.buttonContainer}>
        <TouchableOpacity 
          style={[styles.button, isEditing ? styles.cancelButton : styles.editButton]} 
          onPress={() => setIsEditing(!isEditing)}
        >
          <Text style={styles.buttonText}>
            {isEditing ? 'Annuler' : 'Modifier'}
          </Text>
        </TouchableOpacity>

        <TouchableOpacity 
          style={[styles.button, styles.deleteButton]} 
          onPress={handleDelete}
        >
          <Text style={styles.buttonText}>Supprimer</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: '#fff',
  },
  form: {
    marginBottom: 20,
  },
  infoRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 15,
    padding: 10,
    backgroundColor: '#f8f8f8',
    borderRadius: 8,
  },
  label: {
    fontSize: 16,
    fontWeight: '600',
    marginBottom: 5,
  },
  value: {
    fontSize: 16,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ddd',
    padding: 10,
    borderRadius: 8,
    marginBottom: 15,
  },
  switchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    marginBottom: 15,
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 20,
  },
  button: {
    flex: 1,
    padding: 15,
    borderRadius: 8,
    marginHorizontal: 5,
  },
  editButton: {
    backgroundColor: '#007AFF',
  },
  cancelButton: {
    backgroundColor: '#666',
  },
  deleteButton: {
    backgroundColor: '#FF3B30',
  },
  saveButton: {
    backgroundColor: '#34C759',
    padding: 15,
    borderRadius: 8,
    marginTop: 10,
  },
  buttonText: {
    color: 'white',
    textAlign: 'center',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default VisitDetailScreen;
