import React, { useState, useEffect } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { ActivityIndicator, View, StyleSheet } from 'react-native';

import LoginScreen from './screens/auth/LoginScreen';
import HomeScreen from './screens/home/HomeScreen';
import VisitListScreen from './screens/visits/VisitListScreen';
import AddVisitScreen from './screens/visits/AddVisitScreen';
import VisitDetailScreen from './screens/visits/VisitDetailScreen';

const Stack = createStackNavigator();

const App = () => {
  const [isReady, setIsReady] = useState(false);
  const [initialRouteName, setInitialRouteName] = useState('Login');

  useEffect(() => {
    // Vérifier si un utilisateur est déjà connecté
    const checkLoginState = async () => {
      try {
        const token = await AsyncStorage.getItem('userToken');
        if (token) {
          setInitialRouteName('Home');
        }
      } catch (error) {
        console.log('Erreur lors de la vérification du token:', error);
      } finally {
        setIsReady(true);
      }
    };

    checkLoginState();
  }, []);

  if (!isReady) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#1e3c72" />
      </View>
    );
  }

  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName={initialRouteName}>
        <Stack.Screen 
          name="Login" 
          component={LoginScreen}
          options={{ headerShown: false }}
        />
        <Stack.Screen 
          name="Home" 
          component={HomeScreen}
          options={{ headerShown: false }}
        />
        <Stack.Screen 
          name="VisitList" 
          component={VisitListScreen}
          options={{ title: 'Mes Visites' }}
        />
        <Stack.Screen 
          name="AddVisit" 
          component={AddVisitScreen}
          options={{ title: 'Nouvelle Visite' }}
        />
        <Stack.Screen 
          name="VisitDetail" 
          component={VisitDetailScreen}
          options={{ title: 'Détail de la visite' }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
};

const styles = StyleSheet.create({
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f9f9f9'
  }
});

export default App;
