import React from 'react';
import { 
  View, 
  Text, 
  TouchableOpacity, 
  Image, 
  StyleSheet, 
  SafeAreaView,
  Alert
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { CommonActions } from '@react-navigation/native';

const HomeScreen = ({ navigation }) => {
  // Version ultra-simplifiée de déconnexion
  const logoutNow = () => {
    // Suppression immédiate du token sans promesse
    AsyncStorage.removeItem('userToken')
      .then(() => {
        console.log("Token supprimé avec succès");
        // Navigation forcée vers Login
        navigation.dispatch(
          CommonActions.reset({
            index: 0,
            routes: [{ name: 'Login' }],
          })
        );
      })
      .catch(error => {
        console.error("Erreur lors de la suppression du token:", error);
      });
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.content}>
        <Text style={styles.title}>Suivi des Visites</Text>
        <Text style={styles.description}>
          Application mobile pour les visiteurs médicaux
        </Text>

        {/* Boutons de navigation */}
        <View style={styles.buttonContainer}>
          <TouchableOpacity
            style={styles.button}
            onPress={() => navigation.navigate('VisitList')}
          >
            <Text style={styles.buttonText}>Voir les visites</Text>
          </TouchableOpacity>
          
          <TouchableOpacity
            style={styles.button}
            onPress={() => navigation.navigate('AddVisit')}
          >
            <Text style={styles.buttonText}>Ajouter une visite</Text>
          </TouchableOpacity>
          
          {/* Bouton de déconnexion simplifié */}
          <TouchableOpacity
            style={[styles.button, styles.logoutButton]}
            onPress={logoutNow}
          >
            <Text style={styles.logoutText}>DÉCONNEXION</Text>
          </TouchableOpacity>
        </View>
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff'
  },
  content: {
    flex: 1,
    padding: 20,
    justifyContent: 'center',
    alignItems: 'center'
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
    color: '#1e3c72'
  },
  description: {
    fontSize: 16,
    color: '#666',
    marginBottom: 30,
    textAlign: 'center'
  },
  buttonContainer: {
    width: '100%',
    alignItems: 'center'
  },
  button: {
    width: '80%',
    backgroundColor: '#1e3c72',
    padding: 15,
    borderRadius: 8,
    marginVertical: 10,
    alignItems: 'center'
  },
  buttonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold'
  },
  logoutButton: {
    backgroundColor: '#d32f2f',
    marginTop: 30
  },
  logoutText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold'
  }
});

export default HomeScreen;
