import React, { useState, useCallback, useMemo } from 'react';
import { 
  View, 
  Text, 
  FlatList, 
  StyleSheet, 
  TouchableOpacity, 
  RefreshControl,
  ActivityIndicator
} from 'react-native';
import { useFocusEffect } from '@react-navigation/native';
import { fetchVisites } from '../../services/api';

interface VisitItem {
  id_visite: number;
  date_visite: string;
  heure_arrivee: string;
  nom_medecin: string;
  prenom_medecin: string;
  rendez_vous: boolean;
  temps_visite: string;
}

const VisitListScreen = ({ navigation }) => {
  const [visits, setVisits] = useState<VisitItem[]>([]);
  const [refreshing, setRefreshing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const loadVisites = async () => {
    try {
      setError(null);
      const data = await fetchVisites();
      setVisits(Array.isArray(data) ? data : []);
    } catch (err) {
      setError('Erreur lors du chargement des visites');
      console.error('Erreur loadVisites:', err);
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  useFocusEffect(
    useCallback(() => {
      loadVisites();
    }, [])
  );

  const onRefresh = useCallback(() => {
    setRefreshing(true);
    loadVisites();
  }, []);

  const sortedVisits = useMemo(() => {
    return [...visits].sort((a, b) => 
      new Date(b.date_visite).getTime() - new Date(a.date_visite).getTime()
    );
  }, [visits]);

  const renderVisitCard = ({ item }: { item: VisitItem }) => {
    const visitDate = new Date(item.date_visite);
    const isToday = new Date().toDateString() === visitDate.toDateString();
    const isFuture = visitDate > new Date();

    return (
      <TouchableOpacity 
        style={[
          styles.card,
          isToday && styles.todayCard,
          isFuture && styles.futureCard
        ]}
        onPress={() => navigation.navigate('VisitDetail', { visitId: item.id_visite })}
      >
        <View style={styles.cardHeader}>
          <Text style={styles.dateText}>
            {visitDate.toLocaleDateString('fr-FR', {
              weekday: 'long',
              day: 'numeric',
              month: 'long'
            })}
          </Text>
          <View style={[
            styles.badge,
            item.rendez_vous ? styles.rdvBadge : styles.noRdvBadge
          ]}>
            <Text style={[
              styles.badgeText,
              item.rendez_vous ? styles.rdvText : styles.noRdvText
            ]}>
              {item.rendez_vous ? 'RDV' : 'Sans RDV'}
            </Text>
          </View>
        </View>

        <View style={styles.cardBody}>
          <Text style={styles.doctorName}>
            Dr. {item.prenom_medecin} {item.nom_medecin}
          </Text>
          <Text style={styles.timeInfo}>
            {new Date(item.heure_arrivee).toLocaleTimeString('fr-FR', {
              hour: '2-digit',
              minute: '2-digit'
            })}
            {item.temps_visite && ` â€¢ ${item.temps_visite.substring(0, 5)}`}
          </Text>
        </View>
      </TouchableOpacity>
    );
  };

  if (loading && !refreshing) {
    return (
      <View style={styles.centerContainer}>
        <ActivityIndicator size="large" color="#1e3c72" />
      </View>
    );
  }

  if (error) {
    return (
      <View style={styles.centerContainer}>
        <Text style={styles.errorText}>{error}</Text>
        <TouchableOpacity style={styles.retryButton} onPress={loadVisites}>
          <Text style={styles.retryText}>RÃ©essayer</Text>
        </TouchableOpacity>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <FlatList
        data={sortedVisits}
        renderItem={renderVisitCard}
        keyExtractor={item => `visit-${item.id_visite}`}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
        ListEmptyComponent={
          <View style={styles.emptyContainer}>
            <Text style={styles.emptyText}>Aucune visite trouvÃ©e</Text>
          </View>
        }
        contentContainerStyle={styles.listContent}
        initialNumToRender={10}
        maxToRenderPerBatch={10}
        windowSize={5}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
  },
  centerContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  listContent: {
    padding: 15,
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: 12,
    padding: 15,
    marginBottom: 12,
    elevation: 2,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
  },
  todayCard: {
    borderLeftWidth: 4,
    borderLeftColor: '#4CAF50',
  },
  futureCard: {
    borderLeftWidth: 4,
    borderLeftColor: '#2196F3',
  },
  cardHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
  },
  dateText: {
    fontSize: 16,
    fontWeight: '600',
    color: '#1e3c72',
  },
  cardBody: {
    marginTop: 5,
  },
  doctorName: {
    fontSize: 15,
    color: '#333',
    marginBottom: 4,
  },
  timeInfo: {
    fontSize: 14,
    color: '#666',
  },
  badge: {
    paddingHorizontal: 8,
    paddingVertical: 4,
    borderRadius: 12,
  },
  rdvBadge: {
    backgroundColor: '#e8f5e9',
  },
  noRdvBadge: {
    backgroundColor: '#ffebee',
  },
  badgeText: {
    fontSize: 12,
    fontWeight: '600',
  },
  rdvText: {
    color: '#2e7d32',
  },
  noRdvText: {
    color: '#c62828',
  },
  emptyContainer: {
    padding: 20,
    alignItems: 'center',
  },
  emptyText: {
    fontSize: 16,
    color: '#666',
  },
  errorText: {
    color: '#c62828',
    fontSize: 16,
    marginBottom: 20,
  },
  retryButton: {
    backgroundColor: '#1e3c72',
    padding: 12,
    borderRadius: 8,
  },
  retryText: {
    color: '#fff',
    fontSize: 14,
    fontWeight: '600',
  }
});

export default VisitListScreen;
